import React, { useState } from "react";
import axios from "axios";
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Card, CardContent } from './ui/card';

const MeteoritePredictor = () => {
  const [year, setYear] = useState(2025);
  const [predictions, setPredictions] = useState([]);

  const predictMeteorites = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", { year });
      setPredictions([response.data]);  // Set the predictions to state
    } catch (error) {
      console.error("Error making prediction request:", error);
    }
  };

  return (
    <div className="p-4">
      <Card className="w-full max-w-md mx-auto">
        <CardContent className="p-4">
          <h2 className="text-xl font-bold mb-2">Meteorite Strike Predictor</h2>
          <Input
            type="number"
            value={year}
            onChange={(e) => setYear(parseInt(e.target.value))}
            className="mb-4"
          />
          <Button onClick={predictMeteorites} className="w-full">Predict</Button>
          <div className="mt-4">
            {predictions.length > 0 && (
              <div>
                <h3 className="font-semibold">Predicted Strike Locations:</h3>
                {predictions.map((p, index) => (
                  <p key={index}>
                    Year {p.year}: Latitude {p.latitude.toFixed(2)}, Longitude {p.longitude.toFixed(2)}
                  </p>
                ))}
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default MeteoritePredictor;
